Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CAP_DELETE_ASSIGNMENT_SP" (
  P_ASSIGNMENT_ID    IN NUMBER
  )
AS
BEGIN

  -- This script is intended to delete assignments
  -- from the context of all scenarios when plan of record assignment is deleted.
  DELETE FROM PRJ_BLB_SLICES_D_CAP_ETC WHERE PRJ_OBJECT_ID = P_ASSIGNMENT_ID;
  DELETE FROM PRJ_BLB_SLICES_W_CAP_ETC WHERE PRJ_OBJECT_ID = P_ASSIGNMENT_ID;
  DELETE FROM PRJ_BLB_SLICES_M_CAP_ETC WHERE PRJ_OBJECT_ID = P_ASSIGNMENT_ID;
  DELETE FROM CAP_SCENARIO_ASSIGNMENTS WHERE ASSIGNMENT_ID = P_ASSIGNMENT_ID;

END;
