Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CAP_DELETE_INVESTMENT_SP" (
  P_INV_ID    IN NUMBER
  )
AS
BEGIN

  -- This script is intended to delete an investment, team members, tasks, assignments
  -- from the context of any scenario(s) when they are removed from the plan of record
  -- cannot use investment id on slices because the index has scenario id first.
  DELETE FROM PRJ_BLB_SLICES_D_CAP_ALC 
   WHERE PRJ_OBJECT_ID IN (SELECT TEAM_ID FROM CAP_SCENARIO_TEAM WHERE PRPROJECTID = P_INV_ID);
  DELETE FROM PRJ_BLB_SLICES_W_CAP_ALC 
   WHERE PRJ_OBJECT_ID IN (SELECT TEAM_ID FROM CAP_SCENARIO_TEAM WHERE PRPROJECTID = P_INV_ID);
  DELETE FROM PRJ_BLB_SLICES_M_CAP_ALC 
   WHERE PRJ_OBJECT_ID IN (SELECT TEAM_ID FROM CAP_SCENARIO_TEAM WHERE PRPROJECTID = P_INV_ID);
  DELETE FROM PRJ_BLB_SLICES_D_CAP_HARDALC 
   WHERE PRJ_OBJECT_ID IN (SELECT TEAM_ID FROM CAP_SCENARIO_TEAM WHERE PRPROJECTID = P_INV_ID);
  DELETE FROM PRJ_BLB_SLICES_W_CAP_HARDALC 
   WHERE PRJ_OBJECT_ID IN (SELECT TEAM_ID FROM CAP_SCENARIO_TEAM WHERE PRPROJECTID = P_INV_ID);
  DELETE FROM PRJ_BLB_SLICES_M_CAP_HARDALC 
   WHERE PRJ_OBJECT_ID IN (SELECT TEAM_ID FROM CAP_SCENARIO_TEAM WHERE PRPROJECTID = P_INV_ID);

  DELETE FROM PRJ_BLB_SLICES_D_CAP_ETC 
   WHERE PRJ_OBJECT_ID 
      IN (SELECT ASSIGNMENT_ID FROM CAP_SCENARIO_ASSIGNMENTS 
           WHERE TASK_ID 
              IN (SELECT TASK_ID FROM CAP_SCENARIO_TASKS WHERE PROJECT_ID = P_INV_ID ) );

  DELETE FROM PRJ_BLB_SLICES_W_CAP_ETC 
   WHERE PRJ_OBJECT_ID 
      IN (SELECT ASSIGNMENT_ID FROM CAP_SCENARIO_ASSIGNMENTS 
           WHERE TASK_ID 
              IN (SELECT TASK_ID FROM CAP_SCENARIO_TASKS WHERE PROJECT_ID = P_INV_ID ) );

  DELETE FROM PRJ_BLB_SLICES_M_CAP_ETC 
   WHERE PRJ_OBJECT_ID 
      IN (SELECT ASSIGNMENT_ID FROM CAP_SCENARIO_ASSIGNMENTS 
           WHERE TASK_ID 
              IN (SELECT TASK_ID FROM CAP_SCENARIO_TASKS WHERE PROJECT_ID = P_INV_ID ) );

  DELETE FROM CAP_SCENARIO_ASSIGNMENTS 
   WHERE TASK_ID 
      IN (SELECT TASK_ID FROM CAP_SCENARIO_TASKS WHERE PROJECT_ID = P_INV_ID);

  DELETE FROM CAP_SCENARIO_TASKS WHERE PROJECT_ID = P_INV_ID;
  DELETE FROM CAP_SCENARIO_TEAM WHERE PRPROJECTID = P_INV_ID;
  DELETE FROM CAP_SCENARIO_MEMBERS WHERE OBJECT_ID = P_INV_ID;
END;
