Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CAP_DELETE_SCENARIO_SUPP_SP" (
  P_SCENARIO_ID    IN NUMBER
  )
AS
BEGIN

  -- This script is intended to delete an investment, team members, tasks, assignments
  -- from the context of a scenario
  DELETE FROM CAP_SCENARIO_MEMBERS WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM CAP_SCENARIO_TEAM WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM CAP_SCENARIO_TASKS WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM CAP_SCENARIO_ASSIGNMENTS WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_D_CAP_ALC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_W_CAP_ALC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_M_CAP_ALC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_D_CAP_HARDALC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_W_CAP_HARDALC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_M_CAP_HARDALC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_D_CAP_ETC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_W_CAP_ETC WHERE SCENARIO_ID = P_SCENARIO_ID;
  DELETE FROM PRJ_BLB_SLICES_M_CAP_ETC WHERE SCENARIO_ID = P_SCENARIO_ID;

END;
