Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CAP_DELETE_TEAM_SP" (
  P_TEAM_ID    IN NUMBER
  )
AS
BEGIN

  -- This script is intended to delete capacity team member and related data from all scenarios when a team member is deleted 
  -- from the plan of record
  DELETE FROM PRJ_BLB_SLICES_D_CAP_ALC WHERE PRJ_OBJECT_ID = P_TEAM_ID;
  DELETE FROM PRJ_BLB_SLICES_W_CAP_ALC WHERE PRJ_OBJECT_ID = P_TEAM_ID;
  DELETE FROM PRJ_BLB_SLICES_M_CAP_ALC WHERE PRJ_OBJECT_ID = P_TEAM_ID;
  DELETE FROM PRJ_BLB_SLICES_D_CAP_HARDALC WHERE PRJ_OBJECT_ID = P_TEAM_ID;
  DELETE FROM PRJ_BLB_SLICES_W_CAP_HARDALC WHERE PRJ_OBJECT_ID = P_TEAM_ID;
  DELETE FROM PRJ_BLB_SLICES_M_CAP_HARDALC WHERE PRJ_OBJECT_ID = P_TEAM_ID;
  DELETE FROM PRJ_BLB_SLICES_D_CAP_ETC WHERE PRJ_OBJECT_ID IN (SELECT ASSIGNMENT_ID FROM CAP_SCENARIO_ASSIGNMENTS WHERE TEAM_ID = P_TEAM_ID );
  DELETE FROM PRJ_BLB_SLICES_W_CAP_ETC WHERE PRJ_OBJECT_ID IN (SELECT ASSIGNMENT_ID FROM CAP_SCENARIO_ASSIGNMENTS WHERE TEAM_ID = P_TEAM_ID );
  DELETE FROM PRJ_BLB_SLICES_M_CAP_ETC WHERE PRJ_OBJECT_ID IN (SELECT ASSIGNMENT_ID FROM CAP_SCENARIO_ASSIGNMENTS WHERE TEAM_ID = P_TEAM_ID );
  DELETE FROM CAP_SCENARIO_TEAM WHERE TEAM_ID = P_TEAM_ID;
  DELETE FROM CAP_SCENARIO_ASSIGNMENTS WHERE TEAM_ID = P_TEAM_ID;

END;
