Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_ACTION_PARAM_INS_SP" (
   P_ACTION_CODE  		IN   VARCHAR2,
   P_PARAMETER_CODE		IN   VARCHAR2,
   P_NAME   			IN   VARCHAR2,
   P_DESCRIPTION      	IN   VARCHAR2,
   P_PARAMETER_VALUE	IN   VARCHAR2,
   P_CREATED_BY       	IN   NUMBER DEFAULT 1,
   P_SOURCE             IN VARCHAR2 DEFAULT 'static'
)
IS
   V_ACTION_COUNT       NUMBER;
   V_ACTION_ID          NUMBER;
   V_PARAMETER_COUNT	NUMBER;
   V_PARAMETER_ID		NUMBER;
   V_CREATED_DATE		DATE := SYSDATE;
BEGIN

	-- CHECK IF ACTION EXISTS FIRST

	SELECT	 COUNT (*)
	INTO     V_ACTION_COUNT
	FROM     CMN_ACTIONS
	WHERE    ACTION_CODE = P_ACTION_CODE;

   	IF V_ACTION_COUNT = 0 THEN
      RAISE_APPLICATION_ERROR (-20006, 'ACTION DOES NOT EXIST');
   	END IF;

	-- GET ACTION ID
	BEGIN
		 SELECT  ID
		 INTO    V_ACTION_ID
		 FROM    CMN_ACTIONS
		 WHERE   ACTION_CODE = P_ACTION_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

	-- CHECK IF PARAMETER EXISTS

	SELECT   COUNT (*)
	INTO     V_PARAMETER_COUNT
	FROM     CMN_ACTION_PARAMS
	WHERE    ACTION_ID = V_ACTION_ID
	AND		 PARAMETER_CODE = P_PARAMETER_CODE;

   	IF V_PARAMETER_COUNT > 0 THEN
      RAISE_APPLICATION_ERROR (-20006, 'ACTION PARAMETER EXISTS');
   	END IF;


	-- GET NEW PARAMETER ID

	BEGIN
		SELECT   CMN_ACTION_PARAMS_S1.NEXTVAL
		INTO     V_PARAMETER_ID
		FROM     DUAL;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;


	-- ADD NEW ACTION PARAMETER
	INSERT INTO CMN_ACTION_PARAMS
	( ID, ACTION_ID, PARAMETER_CODE, PARAMETER_VALUE,
	  CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY, SOURCE)
	VALUES
	( V_PARAMETER_ID, V_ACTION_ID, P_PARAMETER_CODE, P_PARAMETER_VALUE,
	  V_CREATED_DATE, P_CREATED_BY, V_CREATED_DATE, P_CREATED_BY, P_SOURCE );

	-- ADD LOOKUP VALUE
  CMN_LOOKUPS_INS_SP( 'CMN_ACTION_PARAM_TYPES', P_PARAMETER_VALUE, P_NAME, P_DESCRIPTION, 1, NULL, 1, 1, 0, 1);

END;
