Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_ACTION_PARAM_UPD_SP" (
   P_ACTION_CODE  		IN   VARCHAR2,
   P_PARAMETER_CODE		IN   VARCHAR2,
   P_NAME   			IN   VARCHAR2,
   P_DESCRIPTION      	IN   VARCHAR2,
   P_PARAMETER_VALUE	IN   VARCHAR2 DEFAULT '',
   P_CREATED_BY       	IN   NUMBER DEFAULT 1
)
IS
   V_ACTION_COUNT       NUMBER;
   V_ACTION_ID          NUMBER;
   V_PARAMETER_COUNT	NUMBER;
   V_PARAMETER_ID		NUMBER;
   V_CREATED_DATE		DATE := SYSDATE;
BEGIN

	-- CHECK IF ACTION EXISTS FIRST

	SELECT	 COUNT (*)
	INTO     V_ACTION_COUNT
	FROM     CMN_ACTIONS
	WHERE    ACTION_CODE = P_ACTION_CODE;

   	IF V_ACTION_COUNT = 0 THEN
      RAISE_APPLICATION_ERROR (-20006, 'ACTION DOES NOT EXIST');
   	END IF;

	-- GET ACTION ID
	BEGIN
		 SELECT  ID
		 INTO    V_ACTION_ID
		 FROM    CMN_ACTIONS
		 WHERE   ACTION_CODE = P_ACTION_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

	-- CHECK IF PARAMETER EXISTS

	SELECT   COUNT (*)
	INTO     V_PARAMETER_COUNT
	FROM     CMN_ACTION_PARAMS
	WHERE    ACTION_ID = V_ACTION_ID
	AND		 PARAMETER_CODE = P_PARAMETER_CODE;

   	IF V_PARAMETER_COUNT = 0 THEN
      RAISE_APPLICATION_ERROR (-20006, 'ACTION PARAMETER DOES NOT EXIST');
   	END IF;


	-- GET PARAMETER ID
	BEGIN
		 SELECT  ID
		 INTO    V_PARAMETER_ID
		 FROM    CMN_ACTION_PARAMS
		 WHERE   ACTION_ID = V_ACTION_ID
		 AND	 PARAMETER_CODE = P_PARAMETER_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

	-- UPDATE ACTION PARAMETER
	UPDATE CMN_ACTION_PARAMS
	SET	   PARAMETER_VALUE = P_PARAMETER_VALUE,
		   LAST_UPDATED_DATE = V_CREATED_DATE,
		   LAST_UPDATED_BY = P_CREATED_BY
	WHERE  ID = V_PARAMETER_ID;


	-- UPDATE PARAMETER CAPTIONS
	CMN_CAPTIONS_NLS_UPD_SP( 'CMN_ACTION_PARAMS', V_PARAMETER_ID, P_NAME, P_DESCRIPTION, P_CREATED_BY );

END;
