Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_AUDITS_DELETE_SP" 
	(
		P_OBJECT_NAME			VARCHAR2,
    P_PURGE_AGE       VARCHAR2
	)
	AS
	V_SQL                       VARCHAR2 (1000);
	BEGIN

	V_SQL := 'delete from cmn_audits where object_code= ''' || P_OBJECT_NAME || ''' and created_date <= sysdate - ' || P_PURGE_AGE; 
        V_SQL := V_SQL || ' AND ROWNUM <= 10000';

        LOOP
            EXECUTE IMMEDIATE V_SQL;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;

	V_SQL := 'delete from cmn_audits_tsv where object_code= ''' || P_OBJECT_NAME || ''' and created_date <= sysdate - ' || P_PURGE_AGE; 
        V_SQL := V_SQL || ' AND ROWNUM <= 10000';

        LOOP
            EXECUTE IMMEDIATE V_SQL;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;

	V_SQL := 'DELETE FROM ODF_AUD_MULTI_VAL_LOVS WHERE ID NOT IN (SELECT MAX(ID) MAX_ID FROM ODF_AUD_MULTI_VAL_LOVS GROUP BY OBJECT_INSTANCE_ID, ATTRIBUTE_CODE) AND object_code= ''' || P_OBJECT_NAME || ''' and created_date <= sysdate - ' || P_PURGE_AGE;

        V_SQL := V_SQL || ' AND ROWNUM <= 10000';

        LOOP
            EXECUTE IMMEDIATE V_SQL;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;                 
	END;
