CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_AUTONUM_OBJECTS_INS_SP" ( P_OBJECT_CODE IN VARCHAR2, P_ATTRIBUTE_CODE IN VARCHAR2, P_PARTITION_CODE IN VARCHAR2 DEFAULT 'NIKU.ROOT', P_OBJECT_NAME IN VARCHAR2, P_OBJECT_DESC IN VARCHAR2, P_IS_ACTIVE IN INTEGER DEFAULT 1, P_MAX_LENGTH IN INTEGER DEFAULT 30, P_CREATED_BY IN NUMBER ) IS V_ID NUMBER := -1; V_DEFAULT_COUNTER_LENGTH NUMBER := 8;
CURSOR c_scheme IS SELECT id FROM CMN_AUTONUM_SCHEMES WHERE object_code = p_object_code AND partition_code = p_partition_code AND attribute_code = p_attribute_code;
BEGIN
FOR scheme_rec IN c_scheme LOOP v_id := scheme_rec.id; END LOOP;
IF v_id < 0 THEN SELECT CMN_AUTONUM_SCHEMES_S1.NEXTVAL INTO V_ID FROM DUAL;
INSERT INTO CMN_AUTONUM_SCHEMES ( ID, NAME, OBJECT_CODE, ATTRIBUTE_CODE, PARTITION_CODE, IS_ACTIVE, NEXT_VALUE, MAX_LENGTH, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY ) VALUES ( V_ID, NULL, P_OBJECT_CODE, P_ATTRIBUTE_CODE, P_PARTITION_CODE, P_IS_ACTIVE, NULL, P_MAX_LENGTH, SYSDATE, P_CREATED_BY, SYSDATE, P_CREATED_BY ); CMN_CAPTIONS_NLS_INS_SP ( P_TABLE_NAME => 'CMN_AUTONUM_SCHEMES', P_PK_ID => V_ID, P_NAME => P_OBJECT_NAME, P_DESCRIPTION => P_OBJECT_DESC, P_CREATED_BY => P_CREATED_BY );
IF p_partition_code = 'NIKU.ROOT' THEN
IF p_max_length < v_default_counter_length THEN v_default_counter_length := p_max_length; END IF;
-- Create default segment CMN_AUTONUM_SEGMENTS_INS_SP ( P_Scheme_Id => v_id, P_Seg_Code => 'NUMERIC', P_Length => v_default_counter_length, P_Start_Value => '1', P_Text_Value => NULL, P_Position => 0, P_Is_Autoext => 1); END IF;
END IF; END;
