Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_AUTONUM_SEGMENTS_DEL_SP" (
   P_ID          IN  NUMBER
)
IS
   V_SCHEME_ID       NUMBER;
BEGIN

   -- Get the scheme id for this segment so we can update it later
   SELECT SCHEME_ID
   INTO   V_SCHEME_ID
   FROM   CMN_AUTONUM_SCHEME_SEGMENTS
   WHERE  ID = P_ID;

   DELETE
   FROM   CMN_AUTONUM_SCHEME_SEGMENTS
   WHERE  ID = P_ID;

   -- Update next value for this scheme
   CMN_AUTONUM_SCHEMES_UPD_SP(V_SCHEME_ID, NULL, NULL);
END;
