Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_DROP_INDEX_SP" (v_index_name VARCHAR2)
	AUTHID CURRENT_USER AS
	    index_exists NUMBER := 0;
	BEGIN
	    SELECT COUNT(1) INTO index_exists
	    FROM user_indexes
	    WHERE index_name = UPPER(v_index_name);

	    IF index_exists > 0 THEN
	        EXECUTE IMMEDIATE 'Drop index '|| v_index_name;
	    END IF;
END;
