Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_DROP_PROCEDURE_SP" (v_procedure_name VARCHAR2)
	AUTHID CURRENT_USER AS
	    procedure_exists NUMBER := 0;
	BEGIN
	    SELECT COUNT(1) INTO procedure_exists
	    FROM user_objects
	    WHERE object_name = UPPER(v_procedure_name) 
          AND object_type='PROCEDURE';

	    IF procedure_exists > 0 THEN
	        EXECUTE IMMEDIATE 'Drop procedure '|| v_procedure_name;
	    END IF;
END;
