Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_FEATURE_DELETE_SP" (P_FEATURE_CODE IN VARCHAR2)
			IS
			CNT NUMBER;
      		V_FEATURE_ID NUMBER;

			BEGIN
				-- check the feature existnce with count
				SELECT count(*) INTO CNT FROM CMN_FEATURES WHERE CODE = P_FEATURE_CODE ;

				IF CNT > 0 -- if feature exists
					THEN
           				SELECT ID INTO V_FEATURE_ID FROM CMN_FEATURES WHERE CODE = P_FEATURE_CODE;
						-- delete feature artifacts
						DELETE FROM CMN_FEATURE_ARTIFACTS WHERE FEATURE_ID = V_FEATURE_ID;
						-- delete feature
						DELETE FROM CMN_FEATURES WHERE ID = V_FEATURE_ID;
						-- delete nls
						CMN_CAPTIONS_NLS_DEL_SP( 'CMN_FEATURES', V_FEATURE_ID);
				END IF;

			END;
