Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_INDX_MONITORING_SP" 
(
	P_ENABLE_MONITORING         IN NUMBER
) AUTHID CURRENT_USER 
IS

V_MONITOR_INDEX VARCHAR2(255);
V_INDEX_MONITORING_STATUS VARCHAR2(255);

BEGIN

	IF P_ENABLE_MONITORING = 1 THEN
		V_INDEX_MONITORING_STATUS := 'MONITORING';
	ELSE
		V_INDEX_MONITORING_STATUS := 'NOMONITORING';
	END IF;

	FOR I IN ( SELECT INDEX_NAME FROM USER_INDEXES WHERE  INDEX_TYPE!='LOB') 
		LOOP
			V_MONITOR_INDEX:='ALTER INDEX "'  || I.INDEX_NAME || '"'||V_INDEX_MONITORING_STATUS||'  USAGE' ;
			EXECUTE IMMEDIATE V_MONITOR_INDEX ;
		END LOOP;
END;
