Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_MENU_ITEM_DEL_SP" (
   P_ACTION_CODE  		IN   VARCHAR2
)
IS
   V_ACTION_COUNT       NUMBER;
   V_ACTION_ID          NUMBER;
   V_PARAMETER_ID		    NUMBER;
   V_MENU_ITEM_COUNT    NUMBER;
   V_MENU_ITEM_ID		    NUMBER;
   CURSOR C_ACTION_PARAMS (CP_ACTION_CODE VARCHAR2)
   IS
      SELECT   CMN_ACTION_PARAMS.ID,
               CMN_ACTION_PARAMS.PARAMETER_CODE
      FROM     CMN_ACTIONS, CMN_ACTION_PARAMS
      WHERE    CMN_ACTIONS.ACTION_CODE = CP_ACTION_CODE
	  AND	   CMN_ACTION_PARAMS.ACTION_ID = CMN_ACTIONS.ID;
BEGIN

	-- CHECK IF ACTION EXISTS FIRST

	SELECT	 COUNT (*)
	INTO     V_ACTION_COUNT
	FROM     CMN_ACTIONS
	WHERE    ACTION_CODE = P_ACTION_CODE;

   IF    V_ACTION_COUNT = 0 THEN
     NULL;
   ELSE   

	  -- GET ACTION ID
	  BEGIN
		 SELECT  ID
		 INTO    V_ACTION_ID
		 FROM    CMN_ACTIONS
		 WHERE   ACTION_CODE = P_ACTION_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

    	-- CHECK IF MENU ITEM EXISTS FIRST

    	SELECT	COUNT (*)
    	INTO     V_MENU_ITEM_COUNT
    	FROM     CMN_MENU_ITEMS
    	WHERE    ACTION_ID = V_ACTION_ID;

     IF    V_MENU_ITEM_COUNT = 0 THEN
        NULL;
     ELSE

    	  -- GET MENU ITEM ID
    	  BEGIN
    		 SELECT ID
    		 INTO 	V_MENU_ITEM_ID
    		 FROM	CMN_MENU_ITEMS
    		 WHERE	ACTION_ID = V_ACTION_ID;
    		 EXCEPTION
    		 	WHEN OTHERS THEN
    				 NULL;
    	  END;

    	  -- DELETE MENU ITEM
    	  IF ( NOT(V_MENU_ITEM_ID IS NULL) )
    	  THEN

      	DELETE
	    	FROM  	  CMN_MENU_ITEMS
    		WHERE	  ACTION_ID = V_ACTION_ID;

    	  END IF;

  	  END IF;

   END IF;

END;
