Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_NDE_PAGE_DEL_SP" (
   P_PAGE_CODE		   		IN   VARCHAR2
)
IS
   V_PAGE_ID            	              NUMBER;
   V_MENU_ITEM_ID		     	              NUMBER;
   V_ACTION_ID           	              NUMBER;
   V_TEMPLATE_PAGE_ID          	       	NUMBER;
   V_LAYOUT_ID 				         	       	NUMBER;
   V_POSITION 				         	       	NUMBER;
   V_CREATE_DATE                 				DATE := SYSDATE;
   V_CNT1		               							NUMBER;
   V_PAGE_TYPE_CODE											VARCHAR2(30);
BEGIN

		SELECT   COUNT (*)
		INTO     V_CNT1
		FROM     CMN_PAGES
		WHERE    PAGE_CODE 		= P_PAGE_CODE;

   	IF    V_CNT1 = 0
   	THEN
      RAISE_APPLICATION_ERROR (-20006, 'PAGE DOES NOT EXIST');
   	END IF;

		BEGIN
			select  id, page_type_code
			into    v_page_id, v_page_type_code
			from    cmn_pages
			where		page_code = P_PAGE_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

		BEGIN
			select  id
			into    v_action_id
			from    cmn_actions
			where		action_code = P_PAGE_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

		delete 
		from 		cmn_pages
		where		id = v_page_id;

		delete 
		from 		cmn_page_portlets
		where		page_id = v_page_id;

		cmn_captions_nls_del_sp( 'CMN_PAGES', v_page_id );

		delete 
		from 		cmn_actions
		where		id = v_action_id;

		delete 
		from		cmn_sec_policies
		where		object_instance_id = ''||v_page_id
		and			object_code = 'PAGE'
		and			component_code = 'CMN'
		and			object_type_code = 'PAGE'
		and			permission_code = 'VIEW';

--
--	For tabs delete the menu item
--

		IF ( v_page_type_code = 'tab' )
		THEN
			BEGIN
				select  id
				into    v_menu_item_id
				from    cmn_menu_items
				where		action_id = v_action_id;
			EXCEPTION
				WHEN OTHERS THEN
					NULL;
			END;

			delete
			from		cmn_menu_items
			where		id = V_MENU_ITEM_ID;

			cmn_captions_nls_del_sp( 'CMN_MENU_ITEMS', V_MENU_ITEM_ID );

		END IF;

END;
