Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_NDE_PAGE_PORTLET_INS_SP" (
   P_PAGE_CODE          IN   VARCHAR2,
   P_PORTLET_CODE       IN   VARCHAR2,
   P_PORTLET_COL        IN   NUMBER DEFAULT 0,
   P_PORTLET_ROW        IN   NUMBER DEFAULT 0
)
IS
   V_PAGE_ID                            NUMBER;
   V_PORTLET_ID                         NUMBER;
   V_PAGE_PORTLETS_ID                   NUMBER;
   V_PAGE_PORTLET_STATUS_ID             NUMBER;
   V_CNT1                               NUMBER;
BEGIN
  BEGIN
    SELECT ID INTO V_PAGE_ID FROM CMN_PAGES WHERE PAGE_CODE = P_PAGE_CODE;
  EXCEPTION
    WHEN OTHERS THEN
      RAISE_APPLICATION_ERROR (-20006, 'PAGE DOES NOT EXIST');
  END;

  BEGIN
    SELECT ID INTO V_PORTLET_ID FROM CMN_PORTLETS WHERE PORTLET_CODE = P_PORTLET_CODE;
  EXCEPTION
    WHEN OTHERS THEN
      RAISE_APPLICATION_ERROR (-20006, 'PORTLET DOES NOT EXIST');
  END;

  SELECT COUNT(*) INTO V_CNT1
    FROM CMN_PAGE_PORTLETS
    WHERE PAGE_ID = V_PAGE_ID AND PORTLET_ID = V_PORTLET_ID AND PRINCIPAL_TYPE = 'SYSTEM';

  IF    V_CNT1 > 0
  THEN
    RAISE_APPLICATION_ERROR (-20006, 'PAGE PORTLET ALREADY EXISTS');
  END IF;

  SELECT CMN_PAGE_PORTLETS_S1.NEXTVAL INTO V_PAGE_PORTLETS_ID FROM DUAL;
  insert into cmn_page_portlets (
    id, page_id, portlet_id, principal_type, principal_id, created_date, created_by, last_updated_date, last_updated_by
    ,portlet_column, portlet_row, state, is_mandatory, portlet_title_pk_id, portlet_title_table
  ) values (
    v_page_portlets_id, v_page_id, v_portlet_id, 'SYSTEM', 0, SYSDATE, 1, SYSDATE, 1
    ,P_PORTLET_COL, P_PORTLET_ROW, 0, 0, v_portlet_id, 'CMN_PORTLETS'
  );

  SELECT CMN_PAGE_PORTLET_STATUS_S1.NEXTVAL INTO V_PAGE_PORTLET_STATUS_ID FROM DUAL;
  insert into cmn_page_portlet_status (
    id, page_portlet_id, principal_type, principal_id, state, created_date, created_by, last_updated_date, last_updated_by
  ) values (
    v_page_portlet_status_id, v_page_portlets_id, 'SYSTEM', 0, 0, SYSDATE, 1, SYSDATE, 1
  );
END;
