Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_NDE_PAGE_UPD_SP" (
   P_PAGE_CODE		   		IN   VARCHAR2,
   P_TEMPLATE_CODE	 	  IN   VARCHAR2,
   P_LAYOUT_CODE		 		IN   VARCHAR2,
   P_SPACE_CODE		   		IN   VARCHAR2,
   P_NAME   						IN   VARCHAR2,
   P_DESCRIPTION      	IN   VARCHAR2,
   P_POSITION      			IN   NUMBER DEFAULT 0,
   P_CREATED_BY       	IN   NUMBER DEFAULT 1
)
IS
   V_PAGE_ID            	              NUMBER;
   V_MENU_ITEM_ID		     	              NUMBER;
   V_ACTION_ID           	              NUMBER;
   V_TEMPLATE_PAGE_ID          	       	NUMBER;
   V_LAYOUT_ID 				         	       	NUMBER;
   V_POSITION 				         	       	NUMBER;
   V_CREATE_DATE                 				DATE := SYSDATE;
   V_CNT1		               							NUMBER;
   V_PAGE_TYPE_CODE											VARCHAR2(30);
BEGIN

		SELECT   COUNT (*)
		INTO     V_CNT1
		FROM     CMN_PAGES
		WHERE    PAGE_CODE 		= P_PAGE_CODE;

   	IF    V_CNT1 = 0
   	THEN
      RAISE_APPLICATION_ERROR (-20006, 'PAGE DOES NOT EXIST');
   	END IF;

		BEGIN
			select  id, page_type_code
			into    v_page_id, v_page_type_code
			from    cmn_pages
			where		page_code = P_PAGE_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

		BEGIN
			select  id
			into    v_action_id
			from    cmn_actions
			where		action_code = P_PAGE_CODE;
		EXCEPTION
			WHEN OTHERS THEN
				NULL;
   	END;

		BEGIN
			SELECT  ID
			INTO    V_TEMPLATE_PAGE_ID
			FROM    CMN_PAGES
			where		page_code = P_TEMPLATE_CODE;
		EXCEPTION
			WHEN OTHERS THEN
      	RAISE_APPLICATION_ERROR (-20006, 'TEMPLATE PAGE DOES NOT EXIST');
   	END;

		BEGIN
			SELECT  ID
			INTO    V_LAYOUT_ID
			FROM    CMN_PAGE_LAYOUTS
			where		layout_code = P_LAYOUT_CODE;
		EXCEPTION
			WHEN OTHERS THEN
      	RAISE_APPLICATION_ERROR (-20006, 'PAGE LAYOUT DOES NOT EXIST');
   	END;

		update 	cmn_pages
		set			layout_id = V_LAYOUT_ID,
						template_page_id = V_TEMPLATE_PAGE_ID,
						last_updated_date = V_CREATE_DATE,
						space_code = P_SPACE_CODE,
						last_updated_by = P_CREATED_BY
		where		id = v_page_id;

		cmn_captions_nls_upd_sp( 'CMN_PAGES', V_PAGE_ID, P_NAME, P_DESCRIPTION, P_CREATED_BY );

--
--	For tabs update the menu item
--

		IF ( v_page_type_code = 'tab' )
		THEN
			BEGIN
				select  id
				into    v_menu_item_id
				from    cmn_menu_items
				where		action_id = v_action_id;
			EXCEPTION
				WHEN OTHERS THEN
					NULL;
			END;

			update	cmn_menu_items
			set			position = P_POSITION
			where		id = V_MENU_ITEM_ID;

			cmn_captions_nls_upd_sp( 'CMN_MENU_ITEMS', V_MENU_ITEM_ID, P_NAME, P_DESCRIPTION, P_CREATED_BY );

		END IF;

END;
