Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_NDE_PORTLET_UPD_SP" (
   P_PORTLET_CODE		   IN   VARCHAR2,
   P_VIEW_SOURCE		   IN   VARCHAR2,
   P_SERVICE_TYPE      IN   VARCHAR2,
   P_SERVICE_UDDI   	 IN   VARCHAR2,
   P_COMPONENT_CODE    IN   VARCHAR2,
   P_NAME   					 IN   VARCHAR2,
   P_DESCRIPTION       IN   VARCHAR2,
   P_LAST_UPDATED_BY        IN   NUMBER DEFAULT 1
)
IS
   V_PORTLET_ID            	              NUMBER;
   V_SERVICE_ID		         	              NUMBER;
   V_PORTLET_SERVICE_ID		 	              NUMBER;
   V_CREATE_DATE                 					DATE := SYSDATE;
BEGIN

		BEGIN
			SELECT   ID
			INTO     V_PORTLET_ID
			FROM     CMN_PORTLETS
			WHERE    PORTLET_CODE 		= P_PORTLET_CODE;
		EXCEPTION
			WHEN NO_DATA_FOUND THEN
      	RAISE_APPLICATION_ERROR (-20006, 'PORTLET DOES NOT EXIST');
   	END;

		BEGIN
			SELECT   ID
			INTO     V_SERVICE_ID
			FROM     CMN_SERVICES
			WHERE    SERVICE_CODE 		= P_PORTLET_CODE;
		EXCEPTION
			WHEN NO_DATA_FOUND THEN
      	RAISE_APPLICATION_ERROR (-20006, 'SERVICE DOES NOT EXIST');
   	END;

		BEGIN
			SELECT  ID
			INTO    V_PORTLET_SERVICE_ID
			FROM    CMN_PORTLET_SERVICES
			WHERE   SERVICE_ID 	= V_SERVICE_ID
			AND			PORTLET_ID 	= V_PORTLET_ID;
		EXCEPTION
			WHEN NO_DATA_FOUND THEN
      	RAISE_APPLICATION_ERROR (-20006, 'PORTLET SERVICE DOES NOT EXIST');
   	END;

		UPDATE 	CMN_PORTLETS
		SET			VIEW_SOURCE 			= P_VIEW_SOURCE,
						COMPONENT_CODE 		= P_COMPONENT_CODE,
						LAST_UPDATED_DATE = V_CREATE_DATE,
						LAST_UPDATED_BY 	= P_LAST_UPDATED_BY
		WHERE 	ID = V_PORTLET_ID;

		CMN_CAPTIONS_NLS_UPD_SP( 'CMN_PORTLETS', V_PORTLET_ID, P_NAME, P_DESCRIPTION, P_LAST_UPDATED_BY);

		UPDATE 	CMN_SERVICES
		SET			SERVICE_TYPE_CODE = P_SERVICE_TYPE,
						COMPONENT_CODE 		= P_COMPONENT_CODE,
						UDDI_DESCRIPTION 	= P_SERVICE_UDDI,
						LAST_UPDATED_DATE = V_CREATE_DATE,
						LAST_UPDATED_BY 	= P_LAST_UPDATED_BY
		WHERE 	ID = V_SERVICE_ID;

		CMN_CAPTIONS_NLS_UPD_SP( 'CMN_SERVICES', V_SERVICE_ID, P_NAME, P_DESCRIPTION, P_LAST_UPDATED_BY );

		CMN_CAPTIONS_NLS_UPD_SP( 'CMN_PORTLET_SERVICES', V_PORTLET_SERVICE_ID, P_NAME, P_DESCRIPTION, P_LAST_UPDATED_BY );

END;
