Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_OPTIONS_DEL_SP" (P_OPTION_CODE IN VARCHAR2)
IS
   V_ID                          NUMBER;
BEGIN
   /* Delete lookup values first */
   CMN_OPTION_VALUES_DEL_SP (P_OPTION_CODE);
   SELECT   NVL (MAX (ID), -9)
   INTO     V_ID
   FROM     CMN_OPTIONS
   WHERE    OPTION_CODE = P_OPTION_CODE;
   IF (V_ID != -9)
   THEN
      /* Delete captions */
      CMN_CAPTIONS_NLS_DEL_SP ('CMN_OPTIONS', V_ID);
      /* Delete lookup type */
      DELETE
        FROM CMN_OPTIONS
       WHERE ID = V_ID;
   END IF;
END;
