Record.definition:
CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RECOMPILE_INVALID_OBJECTS" 
      AS        
      CURSOR C_INVALID IS
          SELECT   OBJECT_TYPE,OBJECT_NAME FROM USER_OBJECTS WHERE STATUS = 'INVALID';

      V_TYPE   VARCHAR2 (4000);
      V_SQL    VARCHAR2 (4000);

      BEGIN
          FOR C_INVALID_REC IN C_INVALID
          LOOP
              BEGIN
                  IF C_INVALID_REC.OBJECT_TYPE = 'PACKAGE BODY' THEN
                      V_SQL := 'alter package ' || C_INVALID_REC.OBJECT_NAME || ' compile body';
                  ELSE
                      V_SQL := 'alter ' || C_INVALID_REC.OBJECT_TYPE || ' ' || C_INVALID_REC.OBJECT_NAME || ' compile';
                  END IF;

                  EXECUTE IMMEDIATE V_SQL;
              EXCEPTION
                  WHEN OTHERS
                  THEN
                  DBMS_OUTPUT.PUT_LINE (V_SQL || ' ==> ' || SQLERRM);
              END;
          END LOOP;
      END;
