CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RPT_ASSIGN_PARMS_INS_ID_SP" ( P_CREATED_BY IN NUMBER DEFAULT 1, P_PARAMETER_ID IN VARCHAR2, P_REPORT_ID IN NUMBER, P_RPTPARMLABEL IN VARCHAR2, P_REQUIRED IN NUMBER, P_RODPARMCODE IN VARCHAR2, P_SORT_ORDER IN NUMBER, P_LANGUAGE_CODE IN VARCHAR2 ) AS THE_NEXT_ID_ASSIGN_PARAMETER NUMBER; V_CNT INTEGER := 0; LABEL VARCHAR2(80); BEGIN
SELECT COUNT (*) INTO V_CNT FROM CMN_RPT_ASSIGN_PARAMETERS WHERE PARM_ID = P_PARAMETER_ID AND REPORT_ID = P_REPORT_ID;
IF (V_CNT <= 0) THEN
SELECT CMN_RPT_ASSIGN_PARAMETERS_S1.NEXTVAL INTO THE_NEXT_ID_ASSIGN_PARAMETER FROM DUAL;
INSERT INTO CMN_RPT_ASSIGN_PARAMETERS ( ID, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY, PARM_ID, REPORT_ID, REQUIRED, RODPARMCODE, SORT_ORDER ) SELECT THE_NEXT_ID_ASSIGN_PARAMETER, SYSDATE, P_CREATED_BY, SYSDATE, P_CREATED_BY, P_PARAMETER_ID, P_REPORT_ID, P_REQUIRED, P_RODPARMCODE, P_SORT_ORDER FROM DUAL WHERE NOT EXISTS ( SELECT 'PARAMETER ASSIGNMENT ALREADY EXISTS' FROM CMN_RPT_ASSIGN_PARAMETERS WHERE PARM_ID = P_PARAMETER_ID AND REPORT_ID = P_REPORT_ID );
UPDATE CMN_RPT_REPORTS SET IS_DIRTY = 1, LAST_UPDATED_BY = P_CREATED_BY, LAST_UPDATED_DATE = SYSDATE where ID = P_REPORT_ID;
SELECT DECODE(TRIM(P_RPTPARMLABEL),NULL,CMN_RPT_PARAMETERS_V.PARMCODE, P_RPTPARMLABEL) INTO LABEL FROM CMN_RPT_PARAMETERS_V WHERE ID = P_PARAMETER_ID AND LANGUAGE_CODE = P_LANGUAGE_CODE;
CMN_CAPTIONS_NLS_INS_SP ( 'CMN_RPT_ASSIGN_PARAMETERS', THE_NEXT_ID_ASSIGN_PARAMETER, LABEL, NULL, P_CREATED_BY, NULL, NULL, NULL, NULL, NULL );
END IF;
END CMN_RPT_ASSIGN_PARMS_INS_ID_SP;
