CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RPT_ASSIGN_PARMS_INS_SP" ( P_CREATED_BY IN NUMBER DEFAULT 1, P_PARMCODE IN VARCHAR2, P_REPORT IN VARCHAR2, P_RPTPARMLABEL IN VARCHAR2, P_DEFAULTVAL IN VARCHAR2, P_REQUIRED IN NUMBER, P_RODPARMCODE IN VARCHAR2, P_SORT_ORDER IN NUMBER, P_DEPENDENT_ID IN NUMBER, P_DEPENDENT_SQL IN VARCHAR2, P_LANGUAGE_CODE IN VARCHAR2 DEFAULT 'en' ) AS THE_REPORT_ID NUMBER; THE_PARAMETER_ID NUMBER; THE_NEXT_ID_ASSIGN_PARAMETER NUMBER; V_CNT INTEGER := 0; LABEL VARCHAR2(80);
BEGIN
SELECT CMN_RPT_REPORTS.ID INTO THE_REPORT_ID FROM CMN_RPT_REPORTS,CMN_CAPTIONS_NLS WHERE CMN_CAPTIONS_NLS.NAME = P_REPORT AND CMN_CAPTIONS_NLS.PK_ID = CMN_RPT_REPORTS.ID AND CMN_CAPTIONS_NLS.TABLE_NAME = 'CMN_RPT_REPORTS' AND CMN_CAPTIONS_NLS.LANGUAGE_CODE = P_LANGUAGE_CODE;
SELECT CMN_RPT_PARAMETERS.ID INTO THE_PARAMETER_ID FROM CMN_RPT_PARAMETERS,CMN_CAPTIONS_NLS WHERE CMN_CAPTIONS_NLS.NAME = P_PARMCODE AND CMN_CAPTIONS_NLS.PK_ID = CMN_RPT_PARAMETERS.ID AND CMN_CAPTIONS_NLS.TABLE_NAME = 'CMN_RPT_PARAMETERS' AND CMN_CAPTIONS_NLS.LANGUAGE_CODE = P_LANGUAGE_CODE;
SELECT COUNT (*) INTO V_CNT FROM CMN_RPT_ASSIGN_PARAMETERS WHERE PARM_ID = THE_PARAMETER_ID AND REPORT_ID = THE_REPORT_ID;
IF (V_CNT <= 0) THEN
SELECT CMN_RPT_ASSIGN_PARAMETERS_S1.NEXTVAL INTO THE_NEXT_ID_ASSIGN_PARAMETER FROM DUAL;
INSERT INTO CMN_RPT_ASSIGN_PARAMETERS ( ID, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY, PARM_ID, REPORT_ID, DEFAULTVAL, REQUIRED, RODPARMCODE, SORT_ORDER ) SELECT THE_NEXT_ID_ASSIGN_PARAMETER, SYSDATE, P_CREATED_BY, SYSDATE, P_CREATED_BY, THE_PARAMETER_ID, THE_REPORT_ID, P_DEFAULTVAL, P_REQUIRED, P_RODPARMCODE, P_SORT_ORDER FROM DUAL WHERE NOT EXISTS ( SELECT 'PARAMETER ASSIGNMENT ALREADY EXISTS' FROM CMN_RPT_ASSIGN_PARAMETERS WHERE PARM_ID = THE_PARAMETER_ID AND REPORT_ID = THE_REPORT_ID );
SELECT DECODE(TRIM(P_RPTPARMLABEL),NULL,CMN_RPT_PARAMETERS_V.PARMCODE, P_RPTPARMLABEL) INTO LABEL FROM CMN_RPT_PARAMETERS_V WHERE ID = THE_PARAMETER_ID AND LANGUAGE_CODE = P_LANGUAGE_CODE;
CMN_CAPTIONS_NLS_INS_SP ( 'CMN_RPT_ASSIGN_PARAMETERS', THE_NEXT_ID_ASSIGN_PARAMETER, LABEL, NULL, P_CREATED_BY, NULL, NULL, NULL, NULL, NULL );
END IF;
END CMN_RPT_ASSIGN_PARMS_INS_SP;
