Record.definition:
CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RPT_CATEGORIES_INS_SP" ( P_CREATED_BY IN NUMBER DEFAULT 1, P_IS_ACTIVE IN NUMBER, P_CATEGORY IN VARCHAR2, P_DESCRIPTION IN VARCHAR2, P_LANGUAGE_CODE IN VARCHAR2 DEFAULT 'en' ) AS V_ID NUMBER; V_CNT INTEGER := 0; BEGIN SELECT COUNT (*) INTO V_CNT FROM CMN_RPT_CATEGORIES,CMN_CAPTIONS_NLS WHERE CMN_CAPTIONS_NLS.NAME = P_CATEGORY AND CMN_CAPTIONS_NLS.TABLE_NAME = 'CMN_RPT_CATEGORIES' AND CMN_CAPTIONS_NLS.LANGUAGE_CODE = P_LANGUAGE_CODE ;
IF (V_CNT <= 0) THEN
SELECT CMN_RPT_CATEGORIES_S1.NEXTVAL INTO V_ID FROM DUAL;
INSERT INTO CMN_RPT_CATEGORIES ( ID, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY, IS_ACTIVE ) SELECT V_ID, SYSDATE, P_CREATED_BY, SYSDATE, P_CREATED_BY, P_IS_ACTIVE FROM DUAL WHERE NOT EXISTS ( SELECT 'CATEGORY ALREADY EXISTS' FROM CMN_RPT_CATEGORIES,CMN_CAPTIONS_NLS WHERE CMN_CAPTIONS_NLS.NAME = P_CATEGORY AND CMN_CAPTIONS_NLS.TABLE_NAME = 'CMN_RPT_CATEGORIES' AND CMN_CAPTIONS_NLS.LANGUAGE_CODE = P_LANGUAGE_CODE);
CMN_CAPTIONS_NLS_INS_SP ( 'CMN_RPT_CATEGORIES', V_ID, P_CATEGORY, P_DESCRIPTION, P_CREATED_BY, NULL, NULL, NULL, NULL, NULL );
ELSE RAISE_APPLICATION_ERROR (-20951, 'CATEGORY EXISTS'); END IF;
END CMN_RPT_CATEGORIES_INS_SP;
