Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RPT_LABELS_DEL_SP" (P_LABEL_CODE IN VARCHAR2)
IS
    V_ID NUMBER;
BEGIN
    SELECT   NVL (MAX (ID), -9)
        INTO     V_ID
        FROM     CMN_RPT_LABELS
        WHERE    LABEL_CODE = P_LABEL_CODE;
    IF (V_ID != -9)
    THEN
        CMN_CAPTIONS_NLS_DEL_SP ('CMN_RPT_LABELS', V_ID);
        DELETE FROM CMN_RPT_LABELS WHERE ID = V_ID;
    END IF;
END;
