Record.definition:
CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RPT_LABELS_INS_SP" ( P_LABEL_CODE IN VARCHAR2, P_LABEL_NAME IN VARCHAR2, P_CREATED_BY IN NUMBER DEFAULT 1, P_LANGUAGE_CODE IN VARCHAR2 DEFAULT 'en' ) AS V_ID NUMBER; V_CNT INTEGER := 0; BEGIN SELECT COUNT (*) INTO V_CNT FROM CMN_RPT_LABELS, CMN_CAPTIONS_NLS WHERE CMN_CAPTIONS_NLS.NAME = P_LABEL_NAME AND CMN_CAPTIONS_NLS.TABLE_NAME = 'CMN_RPT_LABELS' AND CMN_CAPTIONS_NLS.LANGUAGE_CODE = P_LANGUAGE_CODE ;
IF (V_CNT <= 0) THEN
SELECT CMN_RPT_LABELS_S1.NEXTVAL INTO V_ID FROM DUAL;
INSERT INTO CMN_RPT_LABELS ( ID, LABEL_CODE, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY ) SELECT V_ID, P_LABEL_CODE, SYSDATE, P_CREATED_BY, SYSDATE, P_CREATED_BY FROM DUAL;
CMN_CAPTIONS_NLS_INS_SP ( 'CMN_RPT_LABELS', V_ID, P_LABEL_NAME, P_LABEL_NAME, P_CREATED_BY, NULL, NULL, NULL, NULL, NULL );
ELSE RAISE_APPLICATION_ERROR (-20951, 'REPORT LABEL EXISTS'); END IF;
END CMN_RPT_LABELS_INS_SP;
