CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RPT_REPORTS_INS_SP" ( P_CREATED_BY IN NUMBER DEFAULT 1, P_IS_ACTIVE IN NUMBER, P_REPORT IN VARCHAR2, P_DESCRIPTION IN VARCHAR2, P_TYPE_ IN VARCHAR2, P_COMPONENT IN NUMBER, P_FILENAME IN VARCHAR2, P_OUTPUT IN VARCHAR2, P_COPIES IN NUMBER, P_LANGUAGE_CODE IN VARCHAR2 DEFAULT 'en' ) AS THE_NEXT_ID_RPT NUMBER; V_CNT INTEGER := 0;
BEGIN
SELECT COUNT (*) INTO V_CNT FROM CMN_RPT_REPORTS,CMN_CAPTIONS_NLS WHERE CMN_CAPTIONS_NLS.NAME = P_REPORT AND CMN_CAPTIONS_NLS.TABLE_NAME = 'CMN_RPT_REPORTS' AND CMN_CAPTIONS_NLS.LANGUAGE_CODE = P_LANGUAGE_CODE;
IF (V_CNT <= 0) THEN
SELECT CMN_RPT_REPORTS_S1.NEXTVAL INTO THE_NEXT_ID_RPT FROM DUAL;
INSERT INTO CMN_RPT_REPORTS ( ID, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY, IS_ACTIVE, TYPE_, COMPONENT ) SELECT THE_NEXT_ID_RPT, SYSDATE, P_CREATED_BY, SYSDATE, P_CREATED_BY, P_IS_ACTIVE, P_TYPE_, P_COMPONENT FROM DUAL WHERE NOT EXISTS ( SELECT 'REPORT ALREADY EXISTS' FROM CMN_RPT_REPORTS,CMN_CAPTIONS_NLS WHERE CMN_CAPTIONS_NLS.NAME = P_REPORT AND CMN_CAPTIONS_NLS.TABLE_NAME = 'CMN_RPT_REPORTS' AND CMN_CAPTIONS_NLS.LANGUAGE_CODE = P_LANGUAGE_CODE );
INSERT INTO CMN_RPT_FILE_ASSIGN ( ID, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY, REPORT_ID, FILENAME, OUTPUT, COPIES ) SELECT CMN_RPT_FILE_ASSIGN_S1.NEXTVAL, SYSDATE, P_CREATED_BY, SYSDATE, P_CREATED_BY, THE_NEXT_ID_RPT, P_FILENAME, P_OUTPUT, P_COPIES FROM DUAL WHERE NOT EXISTS ( SELECT 'REPORT FILE ALREADY EXISTS' FROM CMN_RPT_FILE_ASSIGN WHERE REPORT_ID = THE_NEXT_ID_RPT );
CMN_CAPTIONS_NLS_INS_SP ( 'CMN_RPT_REPORTS', THE_NEXT_ID_RPT, P_REPORT, P_DESCRIPTION, P_CREATED_BY, NULL, NULL, NULL, NULL, NULL );
ELSE RAISE_APPLICATION_ERROR (-20952, 'REPORT EXISTS'); END IF;
END CMN_RPT_REPORTS_INS_SP;
