Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_RPT_RIGHTS_INS_SP" (
  P_GROUP_ROLE_CODE          IN   VARCHAR2,
  P_GROUP_ROLE_NAME          IN   VARCHAR2,
  P_GROUP_ROLE_DESCRIPTION   IN   VARCHAR2,
  P_OBJECT_CODE          IN   VARCHAR2 )
IS
BEGIN
   CMN_SEC_OBJECTS_INS_SP( P_OBJECT_CODE, P_GROUP_ROLE_NAME, P_GROUP_ROLE_DESCRIPTION,'PAGE', 'CMN', 1, 1, 1 );
   CMN_SEC_GROUP_ROLE_INS_SP( P_GROUP_ROLE_CODE, 'ROLE', P_GROUP_ROLE_NAME || ' Report - Run', P_GROUP_ROLE_DESCRIPTION,1,1,'INTERNAL','SYSTEM');
   CMN_SEC_ASSGND_OBJ_PERM_INS_SP(P_OBJECT_CODE,'PAGE','CMN','READ','GROUP',P_GROUP_ROLE_CODE,'admin',1);   
   CMN_SEC_GROUP_HIER_INS_SP( P_GROUP_ROLE_CODE,'REPORTING_ROLE',1);
END;
