Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_SCH_JOBS_SYNC_UPD_SP" (
   p_job_id          NUMBER,
   p_status_code     VARCHAR2,
   p_ret_val     OUT NUMBER
)
IS
   l_job_id NUMBER;
   CURSOR c_job IS
     SELECT id
       FROM CMN_SCH_JOBS
      WHERE id = p_job_id
        AND status_code = 'PROCESSING'
     FOR UPDATE NOWAIT;
BEGIN
   p_ret_val := 0;
   OPEN c_job;
   FETCH c_job INTO l_job_id;

   IF c_job%FOUND
   THEN
     /* Check if the cursor returns any rows; If not, the job has been updated by another database user */
     UPDATE CMN_SCH_JOBS
        SET status_code = p_status_code
      WHERE id = l_job_id;
     p_ret_val := 1;
   ELSE
     p_ret_val := 0;
   END IF;
   COMMIT;
EXCEPTION
   WHEN OTHERS
   THEN
     p_ret_val := 0;
   COMMIT;
END;
