Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_SEC_GROUP_HIERS_TRGU_SP" (
   OLD_GROUP_ID          NUMBER,
   OLD_PARENT_GROUP_ID   NUMBER,
   NEW_GROUP_ID          NUMBER,
   NEW_PARENT_GROUP_ID   NUMBER
)
IS
BEGIN
   IF    OLD_GROUP_ID <> NEW_GROUP_ID
      OR OLD_PARENT_GROUP_ID <> NEW_PARENT_GROUP_ID
   THEN
      CMN_SEC_GROUP_HIERS_TRGD_SP (OLD_GROUP_ID, OLD_PARENT_GROUP_ID);
      CMN_SEC_GROUP_HIERS_TRGI_SP (NEW_GROUP_ID, NEW_PARENT_GROUP_ID);
   END IF;
END;
