Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_SEC_REMOVE_INST_RIGHT_SP" (
   P_PRINCIPAL_TYPE    IN   VARCHAR2,
   P_PRINCIPAL_CODE    IN   VARCHAR2,
   P_RIGHT_CODE        IN   VARCHAR2,
   P_INSTANCE_ID       IN   NUMBER
)
IS
   V_PRINCIPAL_ID                          NUMBER;
   V_RIGHT_ID                              NUMBER;
   V_CNT1                                  NUMBER;
BEGIN

    BEGIN
      SELECT   ID
      INTO     V_RIGHT_ID
      FROM     CMN_SEC_GROUPS
      WHERE    GROUP_CODE = P_RIGHT_CODE;

    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        RAISE_APPLICATION_ERROR (-20003, 'GROUP_CODE FOR RIGHT DOES NOT EXIST');
      WHEN OTHERS THEN
        NULL;
    END;

    IF P_PRINCIPAL_TYPE = 'GROUP'
    THEN
      BEGIN
        SELECT  ID
        INTO    V_PRINCIPAL_ID
        FROM    CMN_SEC_GROUPS
        WHERE   GROUP_CODE = P_PRINCIPAL_CODE;
      EXCEPTION
        WHEN NO_DATA_FOUND THEN
          RAISE_APPLICATION_ERROR (-20004, 'GROUP_CODE FOR GROUP DOES NOT EXIST');
        WHEN OTHERS THEN
          NULL;
      END;
    ELSE
      BEGIN
        SELECT  ID
        INTO    V_PRINCIPAL_ID
        FROM    CMN_SEC_USERS
        WHERE   USER_NAME = P_PRINCIPAL_CODE;
      EXCEPTION
        WHEN NO_DATA_FOUND THEN
          RAISE_APPLICATION_ERROR (-20005, 'USER_NAME DOES NOT EXIST');
        WHEN OTHERS THEN
          NULL;
      END;
    END IF;

    SELECT   COUNT (*)
    INTO     V_CNT1
    FROM     CMN_SEC_ASSGND_OBJ_PERM
    WHERE    PRINCIPAL_TYPE     = P_PRINCIPAL_TYPE
    AND      PRINCIPAL_ID       = V_PRINCIPAL_ID
    AND      RIGHT_ID           = V_RIGHT_ID
    AND      OBJECT_INSTANCE_ID = P_INSTANCE_ID;

    IF    V_CNT1 > 0
    THEN
      BEGIN
        DELETE
        FROM     CMN_SEC_ASSGND_OBJ_PERM
        WHERE    PRINCIPAL_TYPE     = P_PRINCIPAL_TYPE
        AND      PRINCIPAL_ID       = V_PRINCIPAL_ID
        AND      RIGHT_ID           = V_RIGHT_ID
        AND      OBJECT_INSTANCE_ID = P_INSTANCE_ID;
      END;
    END IF;


END;
