CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CMN_UPLOAD_SEED_DATA_SP" ( P_TABLE_NAME VARCHAR2, P_LANGUAGE_CODE VARCHAR2, P_ID NUMBER, P_NAME VARCHAR2, P_DESCRIPTION VARCHAR2 DEFAULT NULL, P_ATTRIBUTE1 VARCHAR2 DEFAULT NULL, P_ATTRIBUTE2 VARCHAR2 DEFAULT NULL, P_ATTRIBUTE3 VARCHAR2 DEFAULT NULL, P_ATTRIBUTE4 VARCHAR2 DEFAULT NULL, P_ATTRIBUTE5 VARCHAR2 DEFAULT NULL ) AS V_ID INTEGER; V_PK_ID INTEGER; V_CMN_CAPTIONS_ID INTEGER; BEGIN SELECT NVL (MAX (PK_ID), -9) INTO V_PK_ID FROM CMN_CAPTIONS_NLS WHERE ID = P_ID;
IF (V_PK_ID != -9)-- Found record THEN -- Verify that to-be-translated record exists SELECT NVL (MAX (ID), -9) INTO V_CMN_CAPTIONS_ID FROM CMN_CAPTIONS_NLS WHERE TABLE_NAME = P_TABLE_NAME AND PK_ID = V_PK_ID AND LANGUAGE_CODE = P_LANGUAGE_CODE;
IF (V_CMN_CAPTIONS_ID != -9)-- Record exists THEN UPDATE CMN_CAPTIONS_NLS SET NAME = SUBSTRB (P_NAME, 1, 80), DESCRIPTION = DECODE ( DESCRIPTION, NAME, SUBSTRB (P_NAME, 1, 240), NULL, NULL, SUBSTRB (P_DESCRIPTION, 1, 240) ), ATTRIBUTE1 = P_ATTRIBUTE1, ATTRIBUTE2 = P_ATTRIBUTE2, ATTRIBUTE3 = P_ATTRIBUTE3, ATTRIBUTE4 = P_ATTRIBUTE4, ATTRIBUTE5 = P_ATTRIBUTE5, LAST_UPDATED_DATE = SYSDATE, LAST_UPDATED_BY = 1 WHERE ID = V_CMN_CAPTIONS_ID; END IF; END IF; END;
