Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CRV_BLB_ROLLOVER_SP" 
	(
	  P_FIELD_ID                NUMBER,
		P_ROLLOVER_FINISH_DATE    DATE DEFAULT NULL,
		P_ROLLOVER_START_DATE     DATE DEFAULT NULL
	)
	AS
	BEGIN
        LOOP
            UPDATE CRV_SQL_CURVES
               SET SLICE_STATUS = 3
             WHERE SLICE_STATUS IS NULL
               AND FIELD_ID = P_FIELD_ID
               AND START_DATE < P_ROLLOVER_FINISH_DATE
               AND FINISH_DATE > P_ROLLOVER_START_DATE
               AND ROWNUM <= 1000;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;
	END;
