Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CRV_BLB_SLICE_PREDELETE_SP" 
	(
		P_PRJ_REQUEST_ID    NUMBER,
		P_TABLE_NAME			  VARCHAR2 DEFAULT 'PRJ_BLB_SLICES',
    P_FROM_DATE		      DATE DEFAULT NULL,
		P_TO_DATE	          DATE DEFAULT NULL,
		P_FIELD_ID          NUMBER,
    P_SCENARIO_ID       NUMBER DEFAULT 0
	)
	AS
	V_SQL                       VARCHAR2 (1000);
	BEGIN
  	--
	  -- This procedure removes any slices that are outside the range that will
	  -- be updated when slices are produced using sql curves
  	--
    V_SQL := 'DELETE ' || P_TABLE_NAME
          || ' WHERE SLICE_REQUEST_ID = ' || P_PRJ_REQUEST_ID
          || ' AND (SLICE_DATE < ''' || P_FROM_DATE || ''' OR SLICE_DATE >= ''' || P_TO_DATE || ''')';

    IF P_SCENARIO_ID > 0
    THEN
        V_SQL := V_SQL || ' AND SCENARIO_ID > 0';
    END IF;
    V_SQL := V_SQL || ' AND ROWNUM <= 10000';

    LOOP
      EXECUTE IMMEDIATE V_SQL;
      EXIT WHEN SQL%ROWCOUNT = 0;
      COMMIT;
    END LOOP;
	END;
