Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CRV_BLB_SLICE_RESET_ALL_SP" 
	(
	  P_FIELD_ID      NUMBER,
		P_STATUS_VALUE	NUMBER
	)
	AS
	   V_SQL                         VARCHAR2 (1000);
	BEGIN
--		V_SQL := 'UPDATE CRV_SQL_CURVES SET SLICE_STATUS = ' || P_STATUS_VALUE || ' WHERE SLICE_STATUS IS NULL AND ROWNUM <= 1000';
		LOOP
      UPDATE CRV_SQL_CURVES
         SET SLICE_STATUS = P_STATUS_VALUE
       WHERE SLICE_STATUS IS NULL
         AND FIELD_ID = P_FIELD_ID
         AND ROWNUM <= 1000;
      EXIT WHEN SQL%ROWCOUNT = 0;
			COMMIT;
		END LOOP;
	END;
