Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CRV_BLB_SLICE_STATUS_SP" (

   P_FIELD_ID              IN   NUMBER,
   P_STATUS                IN   NUMBER
)
IS
   V_ID              NUMBER;
BEGIN

  UPDATE CRV_SQL_CURVES
  SET SLICE_STATUS = 2
  WHERE SLICE_STATUS = P_STATUS
  AND FIELD_ID = P_FIELD_ID
  AND ROWNUM <= 2000;

END;
