Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."CRV_SQL_CURVES_DELETE_SP" 
	(
		P_FIELD_ID          NUMBER,
		P_OBJECT_ID         NUMBER DEFAULT -999,
    P_SCENARIO_ID       NUMBER DEFAULT 0
	)
	AS
	V_SQL                       VARCHAR2 (1000);
	BEGIN
  	--
  	--
    V_SQL := 'DELETE CRV_SQL_CURVES '
          || ' WHERE FIELD_ID = ' || P_FIELD_ID;

    IF P_OBJECT_ID <> -999 THEN
      V_SQL := V_SQL || ' AND OBJECT_ID = ' || P_OBJECT_ID;
    END IF;

    IF P_SCENARIO_ID > 0 THEN
      V_SQL := V_SQL || ' AND SCENARIO_ID = ' || P_SCENARIO_ID;
    END IF;

    V_SQL := V_SQL || ' AND ROWNUM <= 10000';

    LOOP
      EXECUTE IMMEDIATE V_SQL;
      EXIT WHEN SQL%ROWCOUNT = 0;
      COMMIT;
    END LOOP;
	END;
