Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."EXEC_SQL" (STRING IN VARCHAR2) AS
CURSOR_NAME
INTEGER;
RET INTEGER;
BEGIN
 /* Called From:
     Procedure         :-
     Trigger           :-
     PMD TransactionId :-
     WMD ActionId      :-
   Modified By   : Devdatta
   Modified Date : 07/25/2001
*/
	CURSOR_NAME := DBMS_SQL.OPEN_CURSOR;
	BEGIN
		DBMS_SQL.PARSE(CURSOR_NAME,
STRING, DBMS_SQL.native);
		RET := DBMS_SQL.EXECUTE(CURSOR_NAME);
	EXCEPTION
		WHEN
OTHERS THEN
			DBMS_SQL.CLOSE_CURSOR(CURSOR_NAME);
			RAISE;
	END
;	DBMS_SQL.CLOSE_CURSOR(CURSOR_NAME);
END;
