Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."NBI_DATAMART_LIGHT_SP" 
(
    P_EXTRACT_OBS         NUMBER DEFAULT 0,
    P_EXTRACT_CALENDARS   NUMBER DEFAULT 0,
    P_NUM_CAL_DAYS        NUMBER DEFAULT 730
)
AS

BEGIN
    NBI_VALIDATE_SETTINGS_SP;
    IF P_EXTRACT_OBS = 1 THEN
        BEGIN
            EXECUTE IMMEDIATE 'TRUNCATE TABLE NBI_DIM_OBS';
            EXECUTE IMMEDIATE 'TRUNCATE TABLE NBI_DIM_OBS_FLAT';
            NBI_POPULATE_OBS_DIM_SP;
        END;
    END IF;

    IF P_EXTRACT_CALENDARS = 1 THEN
        BEGIN
            NBI_POPULATE_CAL_DIM_SP(P_NUM_CAL_DAYS);
            NBI_POPULATE_FISCAL_DIM_SP;
        END;
    END IF;
END;
