    BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
  bHasGlobalRight := 0;

  select  user_id
  into    cInternalUserId
  from    srm_resources
  where   unique_name = cUserID;


  BEGIN
  StoO_error := 0;
  select 1
  into   bHasGlobalRight
  from dual
  where  exists (SELECT object_id
               FROM   cmn_sec_chk_user_r_v0
               WHERE  object_id = 51560
               AND    user_id = cInternalUserId
               AND    permission_code = 'READ' );
  EXCEPTION
    WHEN NO_DATA_FOUND
    THEN
      NULL;
    WHEN OTHERS
    THEN
      StoO_error := SQLCODE;
      StoO_errmsg := SQLERRM;
  END;


        IF ( PAC_BIL_CMPRINTLOOKUP_SP.cProject IS NOT NULL ) THEN
    BEGIN
         FOR projects_rec IN projects_cursor
         LOOP
         PAC_BIL_GET_BILLING_PROJECT_SP( projects_rec.id,  PAC_BIL_CMPRINTLOOKUP_SP.tempBPCode,  PAC_BIL_CMPRINTLOOKUP_SP.tempEnfCtrAmt,  PAC_BIL_CMPRINTLOOKUP_SP.tempTitle);
         DELETE FROM TEMP_BIL_SUBPROJECTS;
         INSERT INTO TEMP_BIL_SUBPROJECTS (  COMPANY_CODE, PROJECT_CODE,PROJECT_ID, MASTER_PROJECT_ID, BILLING_PROJECT_ID, BILL_AMOUNT, PROJECT_STATUS  )
         SELECT COMPANY_CODE, PROJECT_CODE, ID, ID, ID, 0, NULL FROM PAC_MNT_PROJECTS WHERE PROJECT_CODE = PAC_BIL_CMPRINTLOOKUP_SP.tempBPCode;
         PAC_BIL_GET_SUBPROJECTS_SP ( 0, PAC_BIL_CMPRINTLOOKUP_SP.tempStatus, PAC_BIL_CMPRINTLOOKUP_SP.tempHasSubPrjs );

         V_STMT1 := 'INSERT INTO TEMP_PAC_BIL_CMPRINTLOOKUP (
            InvoiceNo,cmsgroupno,InvoiceDate,InvoiceAmount,Biller,COMPANY_NAME,COMPANY_CODE,
            MinProject, MaxProject, Printed,locationid, status, reprint,billingdate,billing_currency_code,
            HAS_BILLING_SUBPROJECTS,CONSOLIDATED )
        SELECT  I.invoiceno,
            I.CMSGROUPNO,
            I.invoicedate,
            IV.invoiceamount + NVL(IV.taxamount, 0) + NVL(IV.admincharge, 0),
            I.RESOURCE_CODE,
            C.COMPANY_NAME,
            I.COMPANY_CODE,
            MIN(B.PROJECT_CODE),
            MAX(B.PROJECT_CODE),
            SUM(I.printed * 1), C.locationid,
            DECODE(I.printed, 0, ''Unprinted'', 1, ''Printed''),
            I.printed,
            B.billingdate,
            P.billing_currency_code,
            I.HAS_BILLING_SUBPROJECTS,
            I.CONSOLIDATED
         FROM pac_mnt_projects,ppa_Billings B,  invoiceheader I, clntsupp C, invoiceheader_values IV
         WHERE P.COMPANY_CODE = B.COMPANY_CODE
         and   B.PROJECT_CODE = P.PROJECT_CODE
         and   B.COMPANY_CODE= C.COMPANY_CODE
         and C.COMPANY_CODE = I.COMPANY_CODE
         AND I.printed = NVL('''||iPrintedFlag||''', I.printed) 
         and I.invtype = ''C''
         and I.INVOICENO = IV.INVOICENO
         and IV.currency_type = ''BILLING''
         AND I.invoiceno NOT LIKE ''MATCH%''       
         and B.InvoiceNo = I.invoiceno
         and B.TransType = ''BC''
         and ('|| bHasGlobalRight||' = 1) ' ;

 V_STMT2 := ' SELECT  I.invoiceno,
            I.CMSGROUPNO,
            I.invoicedate,
            IV.invoiceamount + NVL(IV.taxamount, 0) + NVL(IV.admincharge, 0),
            I.RESOURCE_CODE,
            C.COMPANY_NAME,
            I.COMPANY_CODE,
            MIN(B.PROJECT_CODE),
            MAX(B.PROJECT_CODE),
            SUM(I.printed * 1), C.locationid,
            DECODE(I.printed, 0, ''Unprinted'', 1, ''Printed''),
            I.printed,
            B.billingdate,
            P.billing_currency_code,
            I.HAS_BILLING_SUBPROJECTS,
            I.CONSOLIDATED
         FROM pac_mnt_projects,ppa_Billings B,  invoiceheader I, clntsupp C, invoiceheader_values IV,cmn_sec_chk_user_inst_v0 inv0
         WHERE P.COMPANY_CODE = B.COMPANY_CODE
         and   B.PROJECT_CODE = P.PROJECT_CODE
         and   B.COMPANY_CODE= C.COMPANY_CODE
         and C.COMPANY_CODE = I.COMPANY_CODE
         AND I.printed = NVL('''||iPrintedFlag||''', I.printed) 
         and I.invtype = ''C''
         and I.INVOICENO = IV.INVOICENO
         and IV.currency_type = ''BILLING''
         AND I.invoiceno NOT LIKE ''MATCH%''      
         and B.InvoiceNo = I.invoiceno
         and B.TransType = ''BC''
         and inv0.object_id = 51560
         AND inv0.user_id ='|| cInternalUserId ||' 
         AND inv0.permission_code = ''READ''
         AND inv0.object_instance_id = P.ID ' ;

IF (dFromDate IS NOT NULL AND dToDate IS NOT NULL) THEN
    EXECUTE IMMEDIATE V_STMT1 || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE ||V_SQL_WHERE_DATE_CLAUSE
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED'
        || ' UNION ' || V_STMT2  || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE ||V_SQL_WHERE_DATE_CLAUSE1
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED' USING dFromDate,dToDate,dFromDate,dToDate;
ELSE
    EXECUTE IMMEDIATE V_STMT1 || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE 
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED'
        || ' UNION ' || V_STMT2  || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE 
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED';
END IF;

    StoO_rowcnt := SQL%ROWCOUNT;
    END LOOP;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            NULL;
        WHEN OTHERS THEN
            StoO_error := SQLCODE;
            StoO_errmsg := SQLERRM;
    END;
    ELSE
    BEGIN
     V_STMT1 := 'INSERT INTO TEMP_PAC_BIL_CMPRINTLOOKUP (
            InvoiceNo,cmsgroupno,InvoiceDate,InvoiceAmount,Biller,COMPANY_NAME,COMPANY_CODE,
            MinProject,MaxProject,Printed,locationid,status,reprint,billingdate,billing_currency_code,
            HAS_BILLING_SUBPROJECTS,CONSOLIDATED )
    SELECT  I.invoiceno,
            I.CMSGROUPNO,
            I.invoicedate,
            IV.invoiceamount + NVL(IV.taxamount, 0) + NVL(IV.admincharge, 0),
            I.RESOURCE_CODE,
            C.COMPANY_NAME,
            I.COMPANY_CODE,
            MIN(B.PROJECT_CODE),
            MAX(B.PROJECT_CODE),
            SUM(I.printed * 1), C.locationid,
            DECODE(I.printed, 0, ''Unprinted'', 1, ''Printed''),
            I.printed,
            B.billingdate,
            P.billing_currency_code,
            I.HAS_BILLING_SUBPROJECTS,
            I.CONSOLIDATED
        FROM    PAC_MNT_PROJECTS P,PPA_BILLINGS B, INVOICEHEADER I, CLNTSUPP C, INVOICEHEADER_VALUES IV
                WHERE       B.COMPANY_CODE = P.COMPANY_CODE
                         AND B.PROJECT_CODE = P.PROJECT_CODE
                         AND B.COMPANY_CODE= C.COMPANY_CODE
                         AND B.TransType  = ''BC''
                         AND C.COMPANY_CODE = I.COMPANY_CODE
                         AND I.INVOICENO = IV.INVOICENO
                         AND B.InvoiceNo = I.invoiceno      
                         AND I.invtype = ''C''
                         AND I.printed = NVL('''||iPrintedFlag||''', I.printed) 
                         AND IV.CURRENCY_TYPE = ''BILLING''
                         AND I.invoiceno NOT LIKE ''MATCH%''
                        AND NOT EXISTS (SELECT  1
                         FROM BILLINGSCHEDULE BS
                         WHERE BS.invoiceno = I.invoiceno
                         AND BS.atf = 1 )
                        AND  ('|| bHasGlobalRight||' = 1) ';

 V_STMT2 := '   SELECT  I.invoiceno,
            I.CMSGROUPNO,
            I.invoicedate,
            IV.invoiceamount + NVL(IV.taxamount, 0) + NVL(IV.admincharge, 0),
            I.RESOURCE_CODE,
            C.COMPANY_NAME,
            I.COMPANY_CODE,
            MIN(B.PROJECT_CODE),
            MAX(B.PROJECT_CODE),
            SUM(I.printed * 1), C.locationid,
            DECODE(I.printed, 0, ''Unprinted'', 1, ''Printed''),
            I.printed,
            B.billingdate,
            P.billing_currency_code,
            I.HAS_BILLING_SUBPROJECTS,
            I.CONSOLIDATED
        FROM    PAC_MNT_PROJECTS P,PPA_BILLINGS B, INVOICEHEADER I, CLNTSUPP C, INVOICEHEADER_VALUES IV,cmn_sec_chk_user_inst_v0 inv0
                WHERE       B.COMPANY_CODE = P.COMPANY_CODE
                         AND B.PROJECT_CODE = P.PROJECT_CODE
                         AND B.COMPANY_CODE= C.COMPANY_CODE
                         AND B.TransType  = ''BC''
                         AND C.COMPANY_CODE = I.COMPANY_CODE
                         AND I.INVOICENO = IV.INVOICENO
                         AND B.InvoiceNo = I.invoiceno      
                         AND I.invtype = ''C''
                         AND I.printed = NVL('''||iPrintedFlag||''', I.printed) 
                         AND IV.CURRENCY_TYPE = ''BILLING''
                         AND I.invoiceno NOT LIKE ''MATCH%''
                        AND NOT EXISTS (SELECT  1
                         FROM BILLINGSCHEDULE BS
                         WHERE BS.invoiceno = I.invoiceno
                         AND BS.atf = 1 )
                         and inv0.object_id = 51560
                             AND inv0.user_id ='|| cInternalUserId ||' 
                             AND inv0.permission_code = ''READ''
                             AND inv0.object_instance_id = P.ID ';

IF (dFromDate IS NOT NULL AND dToDate IS NOT NULL) THEN
    EXECUTE IMMEDIATE V_STMT1 || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE ||V_SQL_WHERE_DATE_CLAUSE
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED'
        || ' UNION ' || V_STMT2  || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE ||V_SQL_WHERE_DATE_CLAUSE1
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED' USING dFromDate,dToDate,dFromDate,dToDate;
ELSE
    EXECUTE IMMEDIATE V_STMT1 || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE 
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED'
        || ' UNION ' || V_STMT2  || V_SQL_WHERE_CLIENT_CLAUSE || V_SQL_WHERE_BILLER_CLAUSE ||V_SQL_WHERE_LOC_CLAUSE ||V_SQL_WHERE_DEPT_CLAUSE||V_SQL_WHERE_PRJ_CLAUSE 
    || ' GROUP BY I.invoiceno, I.CMSGROUPNO,I.invoicedate,IV.invoiceamount,IV.taxamount,
                IV.admincharge,I.RESOURCE_CODE,C.COMPANY_NAME,I.COMPANY_CODE,C.locationid,
                I.printed,B.billingdate,P.billing_currency_code,I.HAS_BILLING_SUBPROJECTS,I.CONSOLIDATED';
END IF;

    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            NULL;
        WHEN OTHERS THEN
            StoO_error := SQLCODE;
            StoO_errmsg := SQLERRM;
    END;
    END IF;
    END;

    BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;

    UPDATE TEMP_PAC_BIL_CMPRINTLOOKUP
      SET Orig_Invoice_Amount = ( SELECT NVL( IV.InvoiceAmount, 0) + NVL( IV.TaxAmount, 0) + NVL( IV.AdminCharge, 0)
                                   FROM InvoiceHeader_Values IV,
                                        InvoiceHeader I
                                   WHERE TEMP_PAC_BIL_CMPRINTLOOKUP.InvoiceNo = I.InvoiceNo AND
                                         I.INVOICENO_APPLYTO = IV.InvoiceNo AND
                                         IV.Currency_Type = 'BILLING' );

    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            NULL;
        WHEN OTHERS THEN
            StoO_error := SQLCODE;
            StoO_errmsg := SQLERRM;
    END;

    OPEN UF1_cursor;
    LOOP
        FETCH UF1_cursor INTO UF1_rowid, UF1_oval1;
        EXIT WHEN UF1_cursor%NOTFOUND;
        BEGIN
        StoO_error   := 0;
        StoO_rowcnt  := 0;
        UPDATE TEMP_PAC_BIL_CMPRINTLOOKUP
        SET
        PROJECT_NAME = UF1_oval1
        WHERE ROWID = UF1_rowid;
        StoO_rowcnt := SQL%ROWCOUNT;
        EXCEPTION
            WHEN NO_DATA_FOUND THEN
                NULL;
            WHEN OTHERS THEN
                StoO_error := SQLCODE;
                StoO_errmsg := SQLERRM;
        END;
    END LOOP;
    CLOSE UF1_cursor;

    iGrouping :=  NVL(iGrouping, 3);
    cDecimalSep :=  NVL(cDecimalSep, '.');
    cThousandSep :=  NVL(cThousandSep, ',');
    IF  iGrouping < 2 THEN
        iGrouping :=  3;
    END IF;

  SELECT   NAME
    INTO cMultProjectText
    FROM CMN_LOOKUPS_V
    WHERE LOOKUP_TYPE = 'PAC_BILLING_MISC_VALUES'
      AND LOOKUP_CODE = 'MULTIPLEPROJECTS'
      AND LANGUAGE_CODE = v_LANGUAGE_CODE;

    BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
    UPDATE TEMP_PAC_BIL_CMPRINTLOOKUP
    SET PROJECT_NAME = cMultProjectText
    WHERE CONSOLIDATED = 1 ;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            NULL;
        WHEN OTHERS THEN
            StoO_error := SQLCODE;
            StoO_errmsg := SQLERRM;
    END;

    BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
    UPDATE TEMP_PAC_BIL_CMPRINTLOOKUP
    SET FmtInvAmount = LTRIM(TO_CHAR(InvoiceAmount));
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            NULL;
        WHEN OTHERS THEN
            StoO_error := SQLCODE;
            StoO_errmsg := SQLERRM;
    END;

    BEGIN
    StoO_error   := 0;
    StoO_rowcnt  := 0;
--       SELECT ID into report_id FROM CMN_SCH_JOB_DEFINITIONS CCHD,CLNTSUPP CLSP WHERE
--                              CCHD.IS_SYSTEM_VISIBLE=1
--                              AND CCHD.JOB_CODE =CLSP.INVOICE_TEMPLATE
--                                AND CLSP.COMPANY_CODE = cClient;

SELECT ID INTO report_id FROM CMN_SCH_JOB_DEFINITIONS WHERE
                                IS_SYSTEM_VISIBLE=1
                                AND (JOB_CODE = 'Customer_Invoice'
                                    OR JOB_CODE = 'RPT_FM_Customer_Invoice');

    UPDATE TEMP_PAC_BIL_CMPRINTLOOKUP
    SET invtype = 1, rptid = report_id;
    StoO_rowcnt := SQL%ROWCOUNT;
    EXCEPTION
        WHEN NO_DATA_FOUND THEN
            NULL;
        WHEN OTHERS THEN
            StoO_error := SQLCODE;
            StoO_errmsg := SQLERRM;
    END;
    IF  NVL(dOverrideDate, '') IS NULL THEN
    BEGIN
        OPEN RC1 FOR
        SELECT InvoiceNo,
               InvoiceDate "Date",
               FmtInvAmount "Amount",
               COMPANY_NAME "COMPANY_NAME",
               PROJECT_NAME "PROJECT_NAME",
               Biller "Biller",
               RTRIM(SUBSTR('No Yes', 1 + ( 3 * SIGN(Printed) ), 3)) "Printed",
               null "OverrideDate",
               COMPANY_CODE "COMPANY_CODE",
               minProject "PROJECT_CODE",
               locationid "Location",
               status "Status",
               reprint,
               invtype,
               rptid,
               cmsgroupno,
               billing_currency_code,
               Orig_Invoice_Amount,
               HAS_BILLING_SUBPROJECTS
        FROM TEMP_PAC_BIL_CMPRINTLOOKUP
        WHERE HAS_BILLING_SUBPROJECTS = NVL( v_HasBilllingSubProjects, HAS_BILLING_SUBPROJECTS)
        ORDER BY 1 , 2 , 4 , 5 ;
    END;
    ELSE
    BEGIN
        OPEN RC1 FOR
        SELECT InvoiceNo,
               InvoiceDate "Date",
               FmtInvAmount "Amount",
               COMPANY_NAME "COMPANY_NAME",
               PROJECT_NAME "PROJECT_NAME",
               Biller "Biller",
               RTRIM(SUBSTR(cNoYesText, 1 + ( 3 * SIGN(Printed)), 3)) "Printed",
               TRUNC(dOverrideDate) "OverrideDate",
               COMPANY_CODE "COMPANY_CODE",
               minProject "PROJECT_CODE",
               locationid "Location",
               status "Status",
               reprint,
               invtype,
               rptid,
               cmsgroupno,
               billing_currency_code,
               Orig_Invoice_Amount,
               HAS_BILLING_SUBPROJECTS
        FROM TEMP_PAC_BIL_CMPRINTLOOKUP
        WHERE HAS_BILLING_SUBPROJECTS = NVL( v_HasBilllingSubProjects, HAS_BILLING_SUBPROJECTS)
        ORDER BY 1 , 2 , 4 , 5 ;
    END;
    END IF;
END PAC_BIL_CMPRINTLOOKUP_SP;
