Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PAC_BIL_PRINT_VALIDATION_SP" (
bBatchMatch 	NUMBER ,
bBillRetainerBalance 	NUMBER ,
bAutoPost 	NUMBER )
AS
StoO_selcnt	INTEGER;
StoO_error 	INTEGER;
StoO_rowcnt	INTEGER;
StoO_crowcnt	INTEGER := 0;
StoO_fetchstatus	INTEGER := 0;
StoO_errmsg	GLOBALPKG.STRING;
StoO_sqlstatus	INTEGER;
message 	GLOBALPKG.STRING;
BEGIN
 /* Called From:
     Procedure         :-
     Trigger           :-
     PMD TransactionId :-
     WMD ActionId      :-
   Modified By   :Nitin
   Modified Date :07/25/01
*/
	IF  ( ( Pac_Bil_Print_Validation_Sp.bBatchMatch = 1) AND   ( Pac_Bil_Print_Validation_Sp.bAutoPost = 1) AND   ( Pac_Bil_Print_Validation_Sp.bBillRetainerBalance = 0)) THEN
	BEGIN
		RAISE_APPLICATION_ERROR(-20710, 'Generate Bill For Retainer Balances not selected');
		RETURN ;
	END;
	END IF;
	IF  ( Pac_Bil_Print_Validation_Sp.bAutoPost = 0) THEN
	BEGIN
		RAISE_APPLICATION_ERROR(-20711, 'Automatically Approve When Applicable not selected');
		RETURN ;
	END;
	END IF;
END Pac_Bil_Print_Validation_Sp;
