CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PAC_CHG_INS_TRANS_VALUES_SP" ( P_TRANS_ID NUMBER, P_CURRENCY_TYPE VARCHAR2, P_CURRENCY_CODE VARCHAR2, P_TOTAL_AMOUNT NUMBER, P_USER_ID NUMBER ) AS V_DATE DATE;
BEGIN V_DATE := SYSDATE;
