Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PAC_MNT_DEL_BCKGRND_PROCESS_SP" (
ID 	NUMBER )
AS
StoO_selcnt	INTEGER;
StoO_error 	INTEGER;
StoO_rowcnt	INTEGER;
StoO_crowcnt	INTEGER := 0;
StoO_fetchstatus	INTEGER := 0;
StoO_errmsg	GLOBALPKG.STRING;
StoO_sqlstatus	INTEGER;
BEGIN
 /* Called From:
     Procedure         :-
     Trigger           :-
     PMD TransactionId :-
     WMD ActionId      :-
   Modified By   :Nitin
   Modified Date :07/25/01
*/

	BEGIN
	StoO_error   := 0;
	StoO_rowcnt  := 0;
	DELETE FROM PAC_MNT_BCKGRND_PROCESSES
	WHERE ID = PAC_MNT_DEL_BCKGRND_PROCESS_SP.ID;
	StoO_rowcnt := SQL%ROWCOUNT;
	EXCEPTION
		WHEN NO_DATA_FOUND THEN
			NULL;
		WHEN OTHERS THEN
			StoO_error := SQLCODE;
			StoO_errmsg := SQLERRM;
	END;
END PAC_MNT_DEL_BCKGRND_PROCESS_SP;
