CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PAC_MNT_INS_ERROR_ARGS_SP" ( P_ERROR_ID NUMBER, P_ARG_INDEX NUMBER, P_ARG_VALUE VARCHAR2, P_FORMAT_TYPE_CODE VARCHAR2, P_USER_ID NUMBER ) AS V_DATE DATE;
BEGIN V_DATE := SYSDATE; INSERT INTO FIN_ERROR_ARGUMENTS ( ID, ERROR_ID, ARG_INDEX, ARG_VALUE, FORMAT_TYPE_CODE, CREATED_DATE, CREATED_BY, LAST_UPDATED_DATE, LAST_UPDATED_BY ) SELECT FIN_ERROR_ARGUMENTS_S1.NEXTVAL, P_ERROR_ID, P_ARG_INDEX, P_ARG_VALUE, P_FORMAT_TYPE_CODE, V_DATE, P_USER_ID, V_DATE, P_USER_ID FROM DUAL;
END PAC_MNT_INS_ERROR_ARGS_SP;
