CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PAC_MNT_INS_ERROR_SP" ( P_ERROR_ID NUMBER, P_MESSAGE_CODE VARCHAR2, P_RESOURCE_ID NUMBER, P_INVESTMENT_ID NUMBER, P_PK_ID NUMBER, P_TABLE_NAME VARCHAR2, P_JOB_RUN_ID NUMBER, P_JOB_CODE VARCHAR2, P_USER_ID NUMBER, P_IS_SYSTEM NUMBER DEFAULT 0 ) AS V_DATE DATE;
BEGIN V_DATE := SYSDATE;
INSERT INTO FIN_ERRORS ( ID, ERROR_CODE, RESOURCE_ID, JOB_RUN_ID, OBJECT_ID, PK_ID, TABLE_NAME, JOB_CODE, IS_SYSTEM, CREATED_DATE, CREATED_BY, LAST_UPDATED_BY, LAST_UPDATED_DATE ) SELECT P_ERROR_ID, P_MESSAGE_CODE, P_RESOURCE_ID, P_JOB_RUN_ID, P_INVESTMENT_ID, P_PK_ID, P_TABLE_NAME, P_JOB_CODE, P_IS_SYSTEM, V_DATE, P_USER_ID, P_USER_ID, V_DATE FROM DUAL; END PAC_MNT_INS_ERROR_SP;
