Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PAC_MNT_VALIDATEUSER_SP" (
p_userid  IN VARCHAR2)
AS
l_resource_id number;
L_FINTIAL_RESOURCE_ID number ;
BEGIN
 IF   PAC_MNT_VALIDATEUSER_SP.p_userid is NOT NULL THEN
  BEGIN
    SELECT id
    INTO   l_resource_id
    FROM   SRM_RESOURCES
    WHERE  USER_ID = PAC_MNT_VALIDATEUSER_SP.p_userid ;
    EXCEPTION
	 WHEN NO_DATA_FOUND THEN
        RAISE_APPLICATION_ERROR(-20074, 'Resource has not been assigned to the logged-in user id.  Please create a resource with financial properties and assign it to the logged-in user id,  after which a logout and login step must be followed.');
        RETURN;
     WHEN OTHERS THEN
        RETURN;
    END;
 ELSE
      RAISE_APPLICATION_ERROR(-20090, 'User id should not be null.');
 END IF;
END PAC_MNT_VALIDATEUSER_SP;
