Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_BLB_INCIDENT_RESET_ALL_SP" (P_STATUS_VALUE	NUMBER)
	AS
	BEGIN
        LOOP
          update prteam
            set INCIDENT_SLICE_STATUS = P_STATUS_VALUE
          where PRID in ( select team.PRID
                        from prteam team inner join srm_resources res on team.PRRESOURCEID = res.ID
                        inner join IMM_INCIDENTS inc on team.PRPROJECTID = inc.INVESTMENT_ID
                            and res.USER_ID = inc.ASSIGNED_TO
                            and team.INCIDENT_SLICE_STATUS IS NULL)
            AND ROWNUM <= 10000;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;
	END;
