Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_BLB_ROLLOVER_TEAM_HARD_SP" 
	(
		P_ROLLOVER_FINISH_DATE    DATE DEFAULT NULL,
		P_ROLLOVER_START_DATE     DATE DEFAULT NULL
	)
	AS
	BEGIN
        LOOP
            UPDATE PRTEAM
            SET HARD_SLICE_STATUS = 3
            WHERE HARD_SLICE_STATUS IS NULL
            AND HARD_START < P_ROLLOVER_FINISH_DATE AND HARD_FINISH > P_ROLLOVER_START_DATE AND ROWNUM <= 10000;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;
	END;
