Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_BLB_ROLL_ASSIGN_ETCCOST_SP" 
	(
		P_ROLLOVER_FINISH_DATE    DATE DEFAULT NULL,
		P_ROLLOVER_START_DATE     DATE DEFAULT NULL
	)
	AS
	BEGIN
        LOOP
            UPDATE PRASSIGNMENT /*+ INDEX (PRASSIGNMENT PRASSIGNMENT_N5)*/ SET ODF_SS_ETCCOST_CURVE = 3 
            WHERE PRSTART < P_ROLLOVER_FINISH_DATE AND PRFINISH > P_ROLLOVER_START_DATE
              AND ODF_SS_ETCCOST_CURVE IS NULL 
              AND ROWNUM <= 10000;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;
	END;
