Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_BLB_SLICE_DELETE_SP" 
	(
		P_PRJ_REQUEST_ID        NUMBER,
		P_DROPOFF_DATE		DATE DEFAULT NULL,
		P_TABLE_NAME			VARCHAR2 DEFAULT 'PRJ_BLB_SLICES',
		P_NEXT_FROM_DATE	DATE DEFAULT NULL,
        P_SCENARIO_ID           NUMBER DEFAULT 0
	)
	AS
	V_SQL                       VARCHAR2 (1000);
	BEGIN
		IF P_DROPOFF_DATE IS NULL
		THEN
			V_SQL := 'DELETE ' || P_TABLE_NAME || ' WHERE SLICE_REQUEST_ID = ' || P_PRJ_REQUEST_ID;
	    ELSE
            IF P_PRJ_REQUEST_ID = -1
            THEN
             V_SQL := 'DELETE ' || P_TABLE_NAME || ' WHERE SLICE_DATE < ''' || P_DROPOFF_DATE || '''';
            ELSE
             V_SQL := 'DELETE ' || P_TABLE_NAME || ' WHERE SLICE_REQUEST_ID = ' || P_PRJ_REQUEST_ID || ' AND (SLICE_DATE < ''' || P_DROPOFF_DATE || ''' OR SLICE_DATE >= ''' || P_NEXT_FROM_DATE || ''')';
            END IF;
        END IF;
        IF P_SCENARIO_ID > 0
        THEN
            V_SQL := V_SQL || ' AND SCENARIO_ID = ' || P_SCENARIO_ID;
        END IF;
        V_SQL := V_SQL || ' AND ROWNUM <= 10000';

        LOOP
            EXECUTE IMMEDIATE V_SQL;
            EXIT WHEN SQL%ROWCOUNT = 0;
            COMMIT;
        END LOOP;
	END;
