Record.definition:
  CREATE OR REPLACE EDITIONABLE PROCEDURE "PPM"."PRJ_BLB_SLICE_RESET_ALL_SP" 
	(
   	P_TABLE_NAME         VARCHAR2,
		P_STATUS_VALUE	NUMBER
	)
	AS
	   V_SQL VARCHAR2 (1000);
	BEGIN
		V_SQL := 'UPDATE ' || P_TABLE_NAME || ' SET SLICE_STATUS = ' || P_STATUS_VALUE || ' WHERE SLICE_STATUS IS NULL AND ROWNUM <= 10000';
		LOOP
    	EXECUTE IMMEDIATE V_SQL;
    	EXIT WHEN SQL%ROWCOUNT = 0;
			COMMIT;
		END LOOP;
	END;
